contracts/GemstoneExtraction.sol.content:
pragma solidity >=0.4.21 <0.6.0;
import "hardhat/console.sol";

contract GemstoneExtraction {
    string public name;
    uint public minedGemCount = 0;

    mapping(uint => MinedGem) public minedGems;

    enum PointOfProcessing {MINED, SALEOFMINEDPRODUCT, PREPARATION, PROCESSING}

    struct MinedGem {
        uint id;
        string gemType;
        uint price;
        address payable miner;
        address payable owner;
        string metadataHash; 
        bool purchased; 
        bool selected;
        string fileURL;
    }

    event GemMining(
        uint id,
        string gemType,
        uint price,
        address payable miner,
        address payable owner,
        string metadataHash,
        bool purchased,
        string fileURL
    );

    event GemProcessing(
        uint id,
        string gemType,
        uint price,
        address payable miner,
        address payable owner,
        string metadataHash,
        bool purchased,
        string fileURL
    );

    event GemPurchased(
        uint id,
        string gemType,
        uint price,
        address payable miner,
        address payable owner,
        string metadataHash,
        bool purchased,
        string fileURL
    );

    event GemSelected(
        uint id,
        string gemType,
        uint price,
        address payable miner,
        address payable owner,
        string metadataHash,
        bool purchased,
        string fileURL
    );

    event MarkNewOwner(
        uint id,
        string gemType,
        uint price,
        address payable miner,
        address payable owner,
        string metadataHash,
        bool purchased,
        string fileURL
    );

    event MarkGemAsSelected(
        uint id,
        string gemType,
        uint price,
        address payable miner,
        address payable owner,
        string metadataHash,
        bool purchased,
        string fileURL
    );

    event TransferGemOwnership(
        uint id,
        string gemType,
        uint price,
        address payable miner,
        address payable owner,
        string metadataHash,
        bool purchased,
        string fileURL
    );

    constructor() public {
        name = "Gemstone Extraction Platform";
    }

    function gemMining(
        string memory _gemType,
        uint _price,
        string memory _metadataHash, 
        bool _purchased,
        string memory _fileURL
    ) public {
        require(bytes(_gemType).length > 0, "Gem type cannot be empty");
        require(_price > 0, "Price must be greater than 0");
        require(bytes(_metadataHash).length > 0, "Metadata hash cannot be empty");

        minedGemCount++;
        minedGems[minedGemCount] = MinedGem(
            minedGemCount,
            _gemType,
            _price,
            msg.sender, 
            msg.sender, 
            _metadataHash, 
            _purchased,
            false, // kezdetben nem választották ki
            _fileURL
        );

        emit GemMining(minedGemCount, _gemType, _price, msg.sender, msg.sender, _metadataHash, _purchased, _fileURL);
    }

    function purchaseGem(uint _id) public payable {
        MinedGem storage _minedGem = minedGems[_id];
        require(_minedGem.id > 0 && _minedGem.id <= minedGemCount, "Invalid gem ID");
        require(_minedGem.purchased == false, "Gem already purchased");

        _minedGem.owner = msg.sender; 
        _minedGem.purchased = true;

        emit GemPurchased(_id, _minedGem.gemType, _minedGem.price, _minedGem.miner, msg.sender, _minedGem.metadataHash, _minedGem.purchased, _minedGem.fileURL);
    }

    function processingGem(uint _id) public {
        MinedGem storage _minedGem = minedGems[_id];
        require(_minedGem.id > 0 && _minedGem.id <= minedGemCount, "Invalid gem ID");
        require(_minedGem.purchased == true, "Gem must be purchased before processing");

        emit GemProcessing(_id, _minedGem.gemType, _minedGem.price, _minedGem.miner, _minedGem.owner, _minedGem.metadataHash, _minedGem.purchased, _minedGem.fileURL);
    }

    function markNewOwner(uint _id) public payable {
        MinedGem storage _minedGem = minedGems[_id];

        require(_minedGem.id > 0 && _minedGem.id <= minedGemCount, "Invalid gem ID");
        require(_minedGem.selected == false, "Gem already selected");
        require(_minedGem.purchased == true, "Gem must be purchased before selection");
        require(msg.value >= _minedGem.price, "Insufficient funds");

        _minedGem.miner.transfer(msg.value);

        _minedGem.owner = msg.sender;
           
           console.log(
            "Transferring from %s to %s %s tokens",
            msg.sender,
            _minedGem.miner,
            _minedGem.price
        );

        emit MarkNewOwner(
            _id, 
            _minedGem.gemType, 
            _minedGem.price, 
            _minedGem.miner, 
            _minedGem.owner, 
            _minedGem.metadataHash, 
            _minedGem.purchased,
            _minedGem.fileURL
        );
    }

    function markGemAsSelected(uint _id) public {
        MinedGem storage _minedGem = minedGems[_id];

        require(_minedGem.id > 0 && _minedGem.id <= minedGemCount, "Invalid gem ID");
        require(_minedGem.selected == false, "Gem already selected");
        require(_minedGem.purchased == true, "Gem must be purchased before selection");

        _minedGem.selected = true;

        emit MarkGemAsSelected(
            _id, 
            _minedGem.gemType, 
            _minedGem.price, 
            _minedGem.miner, 
            _minedGem.owner, 
            _minedGem.metadataHash, 
            _minedGem.purchased,
            _minedGem.fileURL
        );
    }

    function getGemstoneCountByOwner(address _owner) public view returns (uint) {
        uint count = 0;
        for (uint i = 1; i <= minedGemCount; i++) {
            if (minedGems[i].owner == _owner) {
                count++;
            }
        }
        return count;
    }
}

contracts/GemstoneSelecting.sol.content:
pragma solidity >=0.4.21 <0.6.0;

interface IGemstoneExtraction {
    function minedGems(uint) external view returns (uint id, string memory gemType, uint weight, uint height, uint width, uint price, string memory miningLocation, uint miningYear, bool selected, string memory extractionMethod, address payable owner, bool purchased);
}

contract GemstoneSelecting {
    uint public selectedGemCount = 0;
    mapping(uint => SelectedGem) private selectedGems;
    IGemstoneExtraction gemstoneExtraction;
    mapping(uint => bool) public selectedMinedGems;

    struct SelectedGem {
        uint id;
        uint minedGemId;
        uint previousGemId;
        bool forSale;
        string metadataHash; 
        uint price;
        bool used;
        bool replaced; 
        address payable owner;
        address payable gemCutter;
        string fileURL;
    }

    event GemSelecting(
        uint id,
        uint minedGemId,
        string metadataHash,
        bool forSale,
        uint price,
        bool used,
        bool replaced,
        address payable owner,
        address payable gemCutter,
        string fileURL
    );

    event GemToMarket(
        uint id,
        uint minedGemId,
        string metadataHash, 
        bool forSale,
        uint price,
        bool used,
        bool replaced, 
        address payable owner,
        address payable gemCutter,
        string fileURL
    );

    event MarkGemAsUsed(
        uint id,
        uint minedGemId,
        string metadataHash, 
        bool forSale,
        uint price,
        bool used,
        bool replaced, 
        address payable owner,
        address payable gemCutter,
        string fileURL
    );

     event MarkGemAsReplaced(
        uint id,
        uint minedGemId,
        string metadataHash, 
        bool forSale,
        uint price,
        bool used,
        bool replaced, 
        address payable owner,
        address payable gemCutter,
        string fileURL
    );

    event TransferGemOwnership(
        uint id,
        uint minedGemId,
        string metadataHash, 
        bool forSale,
        uint price,
        bool used,
        bool replaced, 
        address payable owner,
        address payable gemCutter,
        string fileURL
    );

    constructor(address _gemstoneExtractionAddress) public {
        gemstoneExtraction = IGemstoneExtraction(_gemstoneExtractionAddress);
    }

    function setPreviousGemId(uint gemId, uint previousGemId) public {
        SelectedGem storage gem = selectedGems[gemId];
        require(gem.id > 0, "Gem does not exist.");
        gem.previousGemId = previousGemId;

        if (previousGemId > 0) {
            SelectedGem storage previousGem = selectedGems[previousGemId];
            previousGem.replaced = true;
        }
    }

    function gemSelecting(
        uint _minedGemId,
        string memory _metadataHash, 
        uint _price,
        string memory _fileURL
    ) public {
        selectedGemCount++;
        SelectedGem storage gem = selectedGems[_minedGemId];
        gem.id = _minedGemId;
        gem.minedGemId = _minedGemId;
        gem.metadataHash = _metadataHash; 
        gem.forSale = false;
        gem.price = _price;
        gem.used = false;
        gem.replaced = false; 
        gem.owner = msg.sender;
        gem.gemCutter = msg.sender;
        gem.fileURL = _fileURL;

        emit GemSelecting(
            gem.id,
            gem.minedGemId,
            gem.metadataHash, 
            gem.forSale,
            gem.price,
            gem.used,
            gem.replaced,
            gem.owner,
            gem.gemCutter,
            gem.fileURL
        );
    }

    function polishGem(uint _id) public {
        SelectedGem storage _selectedGem = selectedGems[_id];
        _selectedGem.forSale = !_selectedGem.forSale;

        emit GemToMarket(
            _selectedGem.id,
            _selectedGem.minedGemId,
            _selectedGem.metadataHash, 
            _selectedGem.forSale,
            _selectedGem.price,
            _selectedGem.used,
            _selectedGem.replaced, 
            _selectedGem.owner,
            _selectedGem.gemCutter,
            _selectedGem.fileURL
        );
    }

    function markGemAsUsed(uint _id) public {
        SelectedGem storage _selectedGem = selectedGems[_id];
        require(_selectedGem.id > 0 && _selectedGem.id <= selectedGemCount, "Invalid gem ID");
        require(_selectedGem.used == false, "Gem already used");
        _selectedGem.used = true;

        emit MarkGemAsUsed(
            _id,
            _selectedGem.minedGemId,
            _selectedGem.metadataHash, 
            _selectedGem.forSale,
            _selectedGem.price,
            _selectedGem.used,
            _selectedGem.replaced, 
            _selectedGem.owner,
            _selectedGem.gemCutter,
            _selectedGem.fileURL
        );
    }

    function getSelectedGemsCountByOwner(address _owner) public view returns (uint) {
        uint count = 0;
        for (uint i = 1; i <= selectedGemCount; i++) {
            if (selectedGems[i].owner == _owner) {
                count++;
            }
        }
        return count;
    }

    function transferGemOwnership(uint _id) public payable {
        SelectedGem storage _selectedGem = selectedGems[_id];
        require(_selectedGem.id > 0 && _selectedGem.id <= selectedGemCount, "Invalid gem ID");
        require(_selectedGem.owner != msg.sender, "You already own this gem");
        require(msg.value >= _selectedGem.price, "Insufficient funds");

        _selectedGem.owner.transfer(msg.value);
        _selectedGem.owner = msg.sender;
        _selectedGem.forSale = false;

        emit TransferGemOwnership(
            _selectedGem.id,
            _selectedGem.minedGemId,
            _selectedGem.metadataHash,
            _selectedGem.forSale,
            _selectedGem.price,
            _selectedGem.used,
            _selectedGem.replaced, 
            _selectedGem.owner,
            _selectedGem.gemCutter,
            _selectedGem.fileURL
        );
    }

    // Public getter for selectedGems
    function getSelectedGem(uint _id) public view returns (
        uint id,
        uint minedGemId,
        uint previousGemId,
        string memory metadataHash,
        bool forSale,
        uint price,
        bool used,
        bool replaced, 
        address owner,
        address gemCutter,
        string memory fileURL
    ) {
        SelectedGem storage gem = selectedGems[_id];
        return (
            gem.id,
            gem.minedGemId,
            gem.previousGemId,
            gem.metadataHash, 
            gem.forSale,
            gem.price,
            gem.used,
            gem.replaced, 
            gem.owner,
            gem.gemCutter,
            gem.fileURL
        );
    }

    function getGemDetails(uint _id) public view returns (
        uint id,
        uint minedGemId,
        uint previousGemId,
        string memory metadataHash, 
        bool forSale,
        uint price,
        bool used,
        bool replaced, 
        address owner,
        address gemCutter,
        string memory fileURL
    ) {
        return getSelectedGem(_id);
    }

      function markGemAsReplaced(uint _id) public {
        SelectedGem storage _selectedGem = selectedGems[_id];
        require(_selectedGem.id > 0 && _selectedGem.id <= selectedGemCount, "Invalid gem ID");
        require(_selectedGem.replaced == false, "Gem already used");
        _selectedGem.replaced = true;

        emit MarkGemAsReplaced(
            _id,
            _selectedGem.minedGemId,
            _selectedGem.metadataHash, 
            _selectedGem.forSale,
            _selectedGem.price,
            _selectedGem.used,
            _selectedGem.replaced, 
            _selectedGem.owner,
            _selectedGem.gemCutter,
            _selectedGem.fileURL
        );
    }
}

contracts/Jewelry.sol.content:
pragma solidity >=0.4.21 <0.6.0;

interface IGemstoneSelecting {
    function selectedGems(uint) external view returns (
        uint id, 
        uint minedGemId, 
        uint weight, 
        uint height, 
        uint width, 
        uint diameter, 
        uint carat, 
        string memory color, 
        string memory gemType, 
        bool forSale, 
        uint price, 
        bool used, 
        address owner
    );
    function markGemAsUsed(uint _id) external;
    function setPreviousGemId(uint gemId, uint previousGemId) external;
    function markGemAsReplaced(uint _id) external;
}

contract Jewelry {
    IGemstoneSelecting gemstoneSelecting;
    uint public jewelryCount = 0;
    mapping(uint => JewelryData) public jewelry;

    struct JewelryData {
        uint id;
        string name;
        uint[] previousGemIds;
        string metadataHash; // Off-chain tárolt fizikai részletek hash-e (pl. IPFS hash)
        bool sale;
        bool processing;
        uint price;
        string fileURL;
        address payable jeweler;
        address payable owner;
        address payable jewOwner;  // Új mező hozzáadva
    }

    event JewelryMaking(
        uint id,
        string name,
        string metadataHash, // Off-chain tárolt fizikai részletek hash-e
        bool sale,
        bool processing,
        uint price,
        string fileURL,
        address payable jeweler,
        address payable owner,
        address payable jewOwner
    );

    event JewelryBought(uint id, address payable newOwner);
    event GemUpdated(uint jewelryId, uint newGemId);
    event GemReplaced(uint jewelryId, uint newGemId);
    event JewelryFinished(uint id, address owner);
    event JewelrySale(uint id, address owner);
    event JewelryAddRepair(uint id, address jewOwner, address jeweler);
    event ReturnToJewOwner(uint id, address jewOwner, address jeweler);

    constructor(address _gemstoneSelectingAddress) public {
        gemstoneSelecting = IGemstoneSelecting(_gemstoneSelectingAddress);
    }

    function jewelryMaking(
        string memory _name,
        uint _gemId,
        string memory _metadataHash, // Off-chain tárolt fizikai részletek hash-e
        bool _sale,
        uint _price,
        string memory _fileURL
    ) public {
        jewelryCount++;

        jewelry[jewelryCount] = JewelryData(
            jewelryCount,
            _name,
            new uint[](0), //Kezdetben üres gem ID-k tömbje
            _metadataHash,    // Off-chain tárolt fizikai részletek hash-e
            _sale,
            true,
            _price,
            _fileURL,
            msg.sender,
            msg.sender,
            msg.sender   // Új jewOwner mező beállítva
        );
        jewelry[jewelryCount].previousGemIds.push(_gemId);

        emit JewelryMaking(
            jewelryCount,
            _name,
            _metadataHash, // Off-chain tárolt fizikai részletek hash-e
            _sale,
            true,
            _price,
            _fileURL,
            msg.sender,
            msg.sender,
            msg.sender  // Új jewOwner mező beállítva
        );
    }

function replaceGem(uint jewelryId, uint oldGemId, uint newGemId) public {
     

    JewelryData storage jew = jewelry[jewelryId];
    require(jew.id > 0, "Jewelry item does not exist.");

    // Ellenőrizzük, hogy az oldGemId létezik-e a previousGemIds tömbben
    bool exists = false;
    for (uint i = 0; i < jew.previousGemIds.length; i++) {
        if (jew.previousGemIds[i] == oldGemId) {
            exists = true;
            break;
        }
    }

    // Hozzáadjuk az új gem ID-t, anélkül hogy a régi gemet eltávolítanánk
    jew.previousGemIds.push(newGemId);

    emit GemReplaced(jewelryId, newGemId);
}


    function getJewelryDetails(uint _id) public view returns (
        uint id,
        string memory name,
        uint[] memory previousGemIds,
        string memory metadataHash, // Off-chain tárolt fizikai részletek hash-e
        bool processing,
        bool sale,
        uint price,
        string memory fileURL,
        address jeweler,
        address owner,
        address jewOwner   // Új mező hozzáadva
    ) {
        JewelryData storage jew = jewelry[_id];
        return (
            jew.id,
            jew.name,
            jew.previousGemIds,
            jew.metadataHash, // Off-chain tárolt fizikai részletek hash-e
            jew.processing,
            jew.sale,
            jew.price,
            jew.fileURL,
            jew.jeweler,
            jew.owner,
            jew.jewOwner   // Új mező hozzáadva
        );
    }

    function buyJewelry(uint _id) public payable {
        JewelryData storage jew = jewelry[_id];
        require(jew.sale, "Jewelry is not for sale");
        require(msg.value >= jew.price, "Insufficient funds");

        jew.owner.transfer(msg.value);
        jew.owner = msg.sender;
        jew.jewOwner = msg.sender;
        jew.sale = false;

        emit JewelryBought(_id, msg.sender);
    }

    function updateGem(uint _jewelryId, uint _newGemId) public {
        JewelryData storage jew = jewelry[_jewelryId];
        require(msg.sender == jew.jeweler, "Only the owner can update the gem");

        jew.previousGemIds.push(_newGemId);

        emit GemUpdated(_jewelryId, _newGemId);
    }

    function getJewelryCountByOwner(address _owner) public view returns (uint) {
        uint count = 0;
        for (uint i = 1; i <= jewelryCount; i++) {
            if (jewelry[i].owner == _owner) {
                count++;
            }
        }
        return count;
    }

    function getJewelryCountByJeweler(address _jeweler) public view returns (uint) {
        uint count = 0;
        for (uint i = 1; i <= jewelryCount; i++) {
            if (jewelry[i].jeweler == _jeweler) {
                count++;
            }
        }
        return count;
    }

    function markedAsFinished(uint _id) public payable {
        JewelryData storage _jewelry = jewelry[_id];
        require(_jewelry.id > 0 && _jewelry.id <= jewelryCount, "Invalid jew ID");
        require(_jewelry.processing == true, "Jewelry already finished");

        _jewelry.processing = false;
        _jewelry.sale = false;

        emit JewelryFinished(_id, _jewelry.owner);
    }

    function markedAsSale(uint _id) public payable {
        JewelryData storage _jewelry = jewelry[_id];
        require(_jewelry.id > 0 && _jewelry.id <= jewelryCount, "Invalid jew ID");

        _jewelry.sale = !_jewelry.sale;

        emit JewelrySale(_id, _jewelry.owner);
    }

    function addForRepair(uint _id) public{
         JewelryData storage jew = jewelry[_id];

        jew.owner = jew.jeweler;
        jew.sale = false;

        emit JewelryAddRepair(_id, jew.jewOwner, jew.jeweler);
    
    }

     function returnToJewOwner(uint _id) public{
         JewelryData storage jew = jewelry[_id];

        jew.owner = jew.jewOwner;
        jew.sale = false;

        emit ReturnToJewOwner(_id, jew.jewOwner, jew.jeweler);
    
    }

}

contracts/Migrations.sol.content:
pragma solidity >=0.4.21 <0.6.0;

contract Migrations {
  address public owner;
  uint public last_completed_migration;

  constructor() public {
    owner = msg.sender;
  }

  modifier restricted() {
    if (msg.sender == owner) _;
  }

  function setCompleted(uint completed) public restricted {
    last_completed_migration = completed;
  }

  function upgrade(address new_address) public restricted {
    Migrations upgraded = Migrations(new_address);
    upgraded.setCompleted(last_completed_migration);
  }
}

contracts/UserRegistry.sol.content:
pragma solidity >=0.4.21 <0.6.0;

contract UserRegistry {
    struct User {
        address userAddress;
        string username;
        string role;
        bytes32 passwordHash;
        bool isRegistered;
    }

    mapping(address => User) private users;

    event UserRegistered(address userAddress, string username, string role);

    function registerUser(string memory _username, string memory _role, bytes32 _passwordHash) public {
        require(!users[msg.sender].isRegistered, "User already registered");
        users[msg.sender] = User(msg.sender, _username, _role, _passwordHash, true);
        emit UserRegistered(msg.sender, _username, _role);
    }

    function isUserRegistered(address _userAddress) public view returns (bool) {
        return users[_userAddress].isRegistered;
    }

    function getUsername(address _userAddress) public view returns (string memory) {
        require(users[_userAddress].isRegistered, "User not registered");
        return users[_userAddress].username;
    }

    function getUserRole(address _userAddress) public view returns (string memory) {
        require(users[_userAddress].isRegistered, "User not registered");
        return users[_userAddress].role;
    }

    function getUserInfo(address _userAddress) public view returns (address, string memory, string memory) {
        require(users[_userAddress].isRegistered, "User not registered");
        User memory user = users[_userAddress];
        return (user.userAddress, user.username, user.role);
    }

    function authenticateUser(address _userAddress, bytes32 _passwordHash) public view returns (bool) {
        require(users[_userAddress].isRegistered, "User not registered");
        return users[_userAddress].passwordHash == _passwordHash;
    }

    // Add this function to get the user's password hash
    function getUserPass(address _userAddress) public view returns (bytes32) {
        require(users[_userAddress].isRegistered, "User not registered");
        return users[_userAddress].passwordHash;
    }
}

